settings.custom_liquid:
{% unless page.metafields.custom.vault_homepage == null %}
  {% assign gids = page.metafields.custom.vault_homepage | split: ',' %}
  {% for gid in gids %}
    {% assign id = gid
      | replace: 'gid://shopify/OnlineStorePage/', '' | replace: '[', '' | replace: '"', '' | replace: ']', '' | plus: 0 %}
    {% for page in pages %}
      {% if page.id == id %}
        <a href="{{ page.url }}">‚Üê Back to the {{ page.title }}</a>
      {% endif %}
    {% endfor %}
  {% endfor %}

{% endunless %}
